import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import api from "../../api";

const ProductDescriptionPage = () => {
  const [product, setProduct] = useState();
  const location = useLocation();
  const path = location.pathname.split("/").filter((x) => x);
  const id = path[path.length - 1];

  console.log(path[path.length - 1]);
  api.catalog.getById(id).then((res) => setProduct(res));
  console.log(product);

  return (
    <div className="container content-center">
      <div className="card w-50 ">
        <img src="..." className="card-img-top" alt="..." />
        <div className="card-body">
          <h5 className="card-title">Заголовок карточки</h5>
          <p className="card-text">
            Небольшой пример текста, который должен основываться на названии
            карты и составлять основную часть содержимого карты.
          </p>
        </div>
        <ul className="list-group list-group-flush">
          <li className="list-group-item">Элемент</li>
          <li className="list-group-item">Второй элемент</li>
          <li className="list-group-item">Третий элемент</li>
        </ul>
        <div className="card-body">
          {/* <a href="#" class="card-link">Ссылка карточки</a>
    <a href="#" class="card-link">Другая ссылка</a> */}
        </div>
      </div>
    </div>
  );
};

export default ProductDescriptionPage;
