import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import api from "../../api";
import Slider from "../ui/Slider";
import Loader from "../ui/Loader";

const ProductDescriptionPage = () => {
  const [product, setProduct] = useState();
  const location = useLocation();
  const path = location.pathname.split("/").filter((x) => x);
  const id = path[path.length - 1];

  console.log(path[path.length - 1]);
  api.allCatalog.getById(id).then((res) => setProduct(res));
  console.log(product);

  return (
    <div className="container">
      <div className="row">
        {product ? (
          <>
            <div className="row">
              <div className="col-md-8 p-auto text-center">
                <Slider images={product.img} sx={{ margin: "auto" }} />
              </div>
              <div className="col-md-4">
                <h4>{`Цена ${product.price} руб.`}</h4>
                <div className="box">
                  <button className="btn btn-primary m-2">В корзину</button>
                </div>
              </div>
            </div>
            <div className="row">
              {product.proportions && (
                <>
                  <h5 className="card-title">{product.name}</h5>
                  <p className="card-text">{product.description}</p>
                </>
              )}
            </div>
            <div className="row">
              <h2>Размеры</h2>
              <img src={product.proportions} className="w-25" alt="" />
            </div>
            <div className="row">
              {product.characteristics && (
                <>
                  <h2>Характеристики</h2>
                  <ul className="list-group list-group-flush">
                    {product.characteristics.map((item) => (
                      <li className="list-group-item">{`- ${item}`}</li>
                    ))}
                  </ul>
                </>
              )}
            </div>
            <div className="row">
              {product.equipment && (
                <>
                  <h2>Комплектация</h2>
                  <ul className="list-group list-group-flush">
                    {product.equipment.map((item) => (
                      <li className="list-group-item">{`- ${item}`}</li>
                    ))}
                  </ul>
                </>
              )}
            </div>
          </>
        ) : (
          <Loader />
        )}
      </div>
    </div>
  );
};

export default ProductDescriptionPage;
