import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import api from "../../api";
import Slider from "../ui/Slider";
import Loader from "../ui/Loader";
import ShoppingBadge from "../ui/badge/ShopingBadge";

const ProductDescriptionPage = () => {
  const [product, setProduct] = useState();
  const location = useLocation();
  const path = location.pathname.split("/").filter((x) => x);
  const id = path[path.length - 1];

  console.log(path[path.length - 1]);
  api.allCatalog.getById(id).then((res) => setProduct(res));
  console.log(product);

  return (
    <div className="container">
      <div className="row">
        {product ? (
          <div className="w-auto">
            {/* <img src={product.img} className="card-img-top" alt="..." /> */}
            <div className="row">
              <div className="col-md-8">
                <Slider
                  images={product.img}
                  sx={{ width: "300px", height: "300px" }}
                />
              </div>
              <div className="col-md-4">
                <h4>{`Цена ${product.price} руб.`}</h4>
                <div className="box">
                  <button className="btn btn-primary m-2">В корзину</button>
                </div>
              </div>
            </div>
            <div className="row">
              <h5 className="card-title">{product.name}</h5>
              <p className="card-text">{product.description}</p>
            </div>
            <ul className="list-group list-group-flush">
              <li className="list-group-item">Элемент</li>
              <li className="list-group-item">Второй элемент</li>
              <li className="list-group-item">Третий элемент</li>
            </ul>
            <div className="row"></div>
          </div>
        ) : (
          <Loader />
        )}
      </div>
    </div>
  );
};

export default ProductDescriptionPage;
