/* eslint-disable react-hooks/exhaustive-deps */
// import { nanoid } from "nanoid";
import React, { useEffect, useState } from "react";
import {
  addObjectToArrayInLocalStorage,
  removeObjectFromArrayById,
} from "../../utils/basket.localStorage";
import { useSelector } from "react-redux";
import { getBasket } from "../../store/catalog";

const Basket = () => {
  // const [count, setCount] = useState(1);
  // const [basket, setBasket] = useState(getDataFromLocalStorage());
  const basket = useSelector(getBasket());
  const [res, setRes] = useState(0);
  console.log("basket", basket);

  // const product = getDataFromLocalStorage();

  useEffect(() => {
    let result = 0;
    basket.map(({ price, count }) => (result += Number(price * count)));
    setRes(result);
  }, [basket]);

  const handleIncrement = (id) => {
    // Клонируем корзину для изменений
    const updatedBasket = basket.map((obj) => ({ ...obj }));
    // Находим объект, который нужно изменить
    const product = updatedBasket.find((obj) => obj._id === id);
    console.log("product", product);
    if (product) {
      // Увеличиваем счетчик на 1
      product.count += 1;
      // Обновляем состояние корзины
      console.log("updatedBasket", updatedBasket);
      // setBasket(updatedBasket);
      updatedBasket.map((arr) => addObjectToArrayInLocalStorage(arr));
    }
  };

  const handleDecrement = (id) => {
    //    // Клонируем корзину для изменений
    const updatedBasket = basket.map((obj) => ({ ...obj }));
    // Находим объект, который нужно изменить
    const product = updatedBasket.find((obj) => obj._id === id);
    console.log("product", product);
    if (product.count > 1) {
      // Уменьшаем счетчик на 1
      product.count -= 1;
      // Обновляем состояние корзины
      console.log("updatedBasket", updatedBasket);
      // setBasket(updatedBasket);
      updatedBasket.map((arr) => addObjectToArrayInLocalStorage(arr));
    }
  };

  const handleDelete = (id) => {
    // setBasket(basket.filter((i) => i._id !== id));
    removeObjectFromArrayById(id);
  };
  return (
    <div className="container">
      <div className="row">
        <div className="col-md-8">
          {basket.map((item) => (
            <div className="card mb-3" style={{ maxwidth: "540px" }}>
              <div className="row g-0">
                <div className="col-md-2">
                  <img
                    src={item.img[0]}
                    className="img-fluid rounded-start"
                    alt={item.name}
                    width="150"
                    height="150"
                  />
                </div>
                <div className="col-md-6 d-flex justify-content-center align-items-center">
                  <div>
                    <p className="p-5">{item.name}</p>
                  </div>
                </div>
                <div className="col-md-4 d-flex justify-content-center align-items-center">
                  <div className="col-6">
                    <button
                      className="btn btn-primary"
                      onClick={() => handleIncrement(item._id)}
                    >
                      <i className="bi bi-plus"></i>
                    </button>
                    <span className="m-2">{item.count}</span>
                    <button
                      className="btn btn-primary "
                      onClick={() => handleDecrement(item._id)}
                    >
                      <i className="bi bi-dash"></i>
                    </button>
                  </div>
                  <div className="col-md-6 d-flex justify-content-center align-items-center">
                    <h6>{`${item.price * item.count} руб.`}</h6>
                    <button
                      onClick={() => handleDelete(item._id)}
                      className="btn"
                      style={{
                        position: "absolute",
                        top: 0,
                        right: 0,
                        margin: "5px",
                      }}
                    >
                      <i className="bi bi-x"></i>
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="col-md-4">
          <div className="card m-2" style={{ width: "300px", height: "150px" }}>
            <h3 className="m-3">{`Итого: ${res} руб.`}</h3>
            <button className="btn btn-primary m-3">
              <p className="m-2">оформить заказ</p>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Basket;
